#ifndef ONLINE_JUDGE
#define debug(...) std::cerr << __LINE__ << ": [", __DEBUG_UTIL__::printer(#__VA_ARGS__, __VA_ARGS__)
#define debugArr(...) std::cerr << __LINE__ << ": [", __DEBUG_UTIL__::printerArr(#__VA_ARGS__, __VA_ARGS__)
#else
#define debug(...)
#define debugArr(...)
#endif

const int MAXN = 200005;
long long fenwick[MAXN];  
int start[MAXN], finish[MAXN];  
vector<int> tree[MAXN];
int timer = 0;
vector<long long> value;  

void update(int idx, long long val, int n) {
    while (idx <= n) {
        fenwick[idx] += val;
        idx += idx & -idx;
    }
}

long long query(int idx) {
    long long sum = 0;
    while (idx > 0) {
        sum += fenwick[idx];
        idx -= idx & -idx;
    }
    return sum;
}

void dfs(int u, int parent) {
    start[u] = ++timer;  
    for (int v : tree[u]) {
        if (v != parent) {
            dfs(v, u);
        }
    }
    finish[u] = timer;  
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    int n, q;
    cin >> n >> q;
    value.resize(n + 1);

    for (int i = 1; i <= n; ++i) {
        cin >> value[i];
    }

    for (int i = 1; i < n; ++i) {
        int a, b;
        cin >> a >> b;
        tree[a].push_back(b);
        tree[b].push_back(a);
    }

    dfs(1, -1);

    for (int i = 1; i <= n; ++i) {
        update(start[i], value[i], n);
    }

    while (q--) {
        int type, s;
        cin >> type >> s;
        if (type == 1) {
            long long x;
            cin >> x;

            update(start[s], x - value[s], n);
            value[s] = x;
        } else if (type == 2) {

            long long result = query(finish[s]) - query(start[s] - 1);
            cout << result << "\n";
        }
    }

    return 0;
}