int main() {
    int n;
    cin >> n;
    vector<int> blocks(n);
    for (int i = 0; i < n; ++i) {
        cin >> blocks[i];
    }

    multiset<int> towers;

    for (int i = 0; i < n; ++i) {
        auto it = towers.upper_bound(blocks[i]);
        if (it != towers.end()) {

            towers.erase(it);
        }

        towers.insert(blocks[i]);
    }

    cout << towers.size() << endl;

    return 0;
}