#pragma GCC target("bmi,avx,avx2")
#define int long long
int v[(int) 1e6 + 5];
int p[(int) 1e6 + 5];
bool mark[(int) 1e6 + 5];
int szv = 0, szp = 0;
pair<int, int> stor[(int) 1e6 + 5];

void sieve() {
    mark[0] = mark[1] = true;
    for(int i = 2; i <= 1e6; i++) {
        if(mark[i] == false) {
            for(int j = i * i; j <= 1e6; j += i) {
                mark[j] = true;
            }
        }
    }
    for(int i = 1; i <= 1e6; i++) {
        if(!mark[i]) {
            p[szp++] = i;
        }
    }
}
int cost(int x, int y) {
    int ans = 0;
    while(x > 0 and y > 0) {
        ans += ((x % 10) ^ (y % 10)) != 0;
        x /= 10;
        y /= 10;
    }
    return ans;
}
bool check(int k) {
    if(k <= 1) return false;
    for(int i = 0; i < szp; i++) {
        int x = p[i];
        if(x * x > k) break;
        if(k % x == 0) return false;
    }
    return true;
}
signed main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    int n;
    cin >> n;
    string s;
    s = to_string(n);
    sieve();
    if(s.size() % 2 == 1) {
        int pw = 1;
        for(int i = 1; i <= s.size() / 2; i++) {
            pw *= 10;
        }
        for(int i = pw / 10; i <= pw - 1; i++) {
            for(int j = 0; j <= 9; j++) {
                string t = to_string(i);
                string num = t;
                num += char(j + '0');
                reverse(t.begin(), t.end());
                num += t;
                szv++;
                v[szv] = stoll(num);
            }
        }
    } else {
        int pw = 1;
        for(int i = 1; i <= s.size() / 2; i++) {
            pw *= 10;
        }
        for(int i = pw / 10; i <= pw - 1; i++) {
            string t = to_string(i);
            string num = t;
            reverse(t.begin(), t.end());
            num += t;
            szv++;
            v[szv] = stoll(num);
        }
    }
    for(int i = 1; i <= szv; i++) {
        stor[i] = {cost(v[i], n), v[i]};
    }
    sort(stor, stor + szv + 1);
    for(int i = 0; i < szv; i++) {
        int num = stor[i].second;
        if(check(num)) {
            cout << stor[i].first << ' ' << stor[i].second;
            return 0;
        }
    }
    cout << -1;
}
