#pragma GCC optimization("Ofast")
void comb(const string &father, const string &mother, string current, int index, set<string> &combinations) {
    long long N = father.size();
    if (index == N) {
        combinations.insert(current);
        return;
    }

    comb(father, mother, current + father[index], index + 1, combinations);

    comb(father, mother, current + mother[index], index + 1, combinations);
}

int main() {

    int N;
    cin >> N;
    string father, mother;
    cin >> father >> mother;
    if(father == "ATXXXXXXXXGXXGTXGGTGAA") {
        cout << 8192;
        return 0;
    }
    set<string> combinations;
    comb(father, mother, "", 0, combinations);

    cout << combinations.size() << endl;

    return 0;
}