const int maxn = 45;
const int maxm = 2e7 + 5;
int n, m, a[maxn], ans = 0;
int cnt[maxm];
void recur1(int pos, int sum) {
    if(pos > n / 2) {
        //cout << sum << endl;
        cnt[sum + (int) 1e7]++;
        return;
    }
    recur1(pos + 1, sum + a[pos]);
    recur1(pos + 1, sum - a[pos]);
    recur1(pos + 1, sum);
}
void recur2(int pos, int sum) {
    if(pos > n) {
        ans += cnt[-sum + (int) 1e7];
        return;
    }
    recur2(pos + 1, sum + a[pos]);
    recur2(pos + 1, sum - a[pos]);
    recur2(pos + 1, sum);
}
int main () {
    cin >> n >> m;
    for(int i = 1; i <= n; i++) cin >> a[i];
    recur1(1, m);
    recur2(n / 2 + 1, 0);
    cout << ans;
}