// Safe multiplication (a * b) % m, avoiding overflow
inline uint64_t mulmodu64(uint64_t a, uint64_t b, uint64_t m) {
    #if defined(__GNUC__) && defined(__x86_64__)
        uint64_t q, r;
        asm("mulq %3;"     // Perform multiplication a * b
            "divq %4;"     // Divide by m
            : "=a"(q), "=d"(r)  // Output: quotient in q, remainder in r
            : "a"(a), "d"(b), "rm"(m) // Inputs: a, b, m
            : "cc");       // Clobber condition codes
        return r;
    #else
        a %= m;
        b %= m;

        // Fast path: no overflow possible
        if (a == 0) return 0;
        if (b <= std::numeric_limits<uint64_t>::max() / a) return (a * b) % m;

        // Overflow-safe modular multiplication
        uint64_t res = 0;
        while (a != 0) {
            if (a & 1) {
                if (b >= m - res) res -= m;
                res += b;
            }

            a >>= 1;
            if (b >= m - b) b += b - m;
            else            b += b;
        }

        return res;
    #endif
}

// Safe exponentiation b^e % m using binary exponentiation
inline uint64_t powmod(uint64_t b, uint64_t e, uint64_t m) {
    uint64_t r = 1;  // Result initialized to 1

    b %= m;
    while (e) {
        if (e & 1) r = mulmodu64(r, b, m);  // Multiply if e is odd
        b = mulmodu64(b, b, m);  // Square the base
        e >>= 1;  // Divide exponent by 2
    }

    return r;
}

uint64_t kth_digit_frac(uint64_t a, uint64_t b, uint64_t k) {
    return 10 * mulmodu64(a, powmod(10, k, b), b) / b;
}


int main() {
    int q;
    cin >> q;
    while(q--) {
        uint64_t a, b, k;
        cin >> a >> b >> k;
        cout << kth_digit_frac(a, b, k - 1) << "\n";
    }
    return 0;
}
