#ifndef ONLINE_JUDGE
#define debug(...) std::cerr << __LINE__ << ": [", __DEBUG_UTIL__::printer(#__VA_ARGS__, __VA_ARGS__)
#define debugArr(...) std::cerr << __LINE__ << ": [", __DEBUG_UTIL__::printerArr(#__VA_ARGS__, __VA_ARGS__)
#else
#define debug(...)
#define debugArr(...)
#endif
#pragma GCC optimize("trapv")
const int MAX_PRIME = 318;
std::vector<int> primes;     
array<int, MAX_PRIME> facts[(int) 1e5 + 5];

void computePrimes() {
    std::vector<bool> is_prime(MAX_PRIME + 1, true);
    is_prime[0] = is_prime[1] = false;

    for (int i = 2; i <= MAX_PRIME; ++i) {
        if (is_prime[i]) {
            primes.push_back(i);
            for (int j = i * 1LL * i; j <= MAX_PRIME; j += i) {
                is_prime[j] = false;
            }
        }
    }
    debug(primes);
}

std::array<int, MAX_PRIME> factorize(int num) {
    std::array<int, MAX_PRIME> factorization = {};
    if (num == 0 || num == 1) {
        return factorization;  
    }
    
    for (int prime : primes) {
        if (prime > num) break;  
        while (num % prime == 0) {
            factorization[prime]++;
            num /= prime;
        }

        if (num == 1) break;  
    }

    if (num > 1 && num < MAX_PRIME) {
        factorization[num]++;
    }

    return factorization;
}

void printFactorization(const std::array<int, MAX_PRIME>& factorization) {
    bool first = true;
    for (int i = 2; i < MAX_PRIME; ++i) {
        if (factorization[i] > 0) {
            if (!first) std::cout << " * ";
            std::cout << i << "^" << factorization[i];
            first = false;
        }
    }
    if (first) {
        std::cout << "1";  
    }
    std::cout << std::endl;
}
#define bigint array<int, MAX_PRIME>
array<int, MAX_PRIME> operator/(array<int, MAX_PRIME> a, array<int, MAX_PRIME> b) {
    for(int i = 0; i < MAX_PRIME; i++) {
        assert(a[i] >= b[i]);
        a[i] -= b[i];
    }
    return a;
}
array<int, MAX_PRIME> operator*(array<int, MAX_PRIME> a, array<int, MAX_PRIME> b) {
    for(int i = 0; i < MAX_PRIME; i++) {
        a[i] += b[i];
    }
    return a;
}
long long binpow(long long base, int exp, int p) {
    long long res = 1;
    while (exp) {
        if (exp & 1) res *= base;
        base *= base;
        base %= p;
        res %= p;
        exp >>= 1;
    }
    return res;
}
int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    computePrimes();
    debug(primes);
    facts[0] = factorize(1);
    for(int i = 1; i <= 1e5; i++) {
        facts[i] = factorize(i) * facts[i - 1];
    }
    int t;
    cin >> t;
    while(t--) {
        int n, k, m;
        cin >> n >> k >> m;
        if(n < k) {
            cout << 0 << '\n';
            continue;
        }
        bigint ans = facts[n];
        ans = ans / (facts[k] * facts[n - k]);
        int res = 1;
        for(int i = 0; i <= MAX_PRIME; i++) {
            res *= binpow(i, ans[i], m);
            res %= m;
        }
        cout << res << '\n';
    }
}
