int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);\
    int n, x;
    cin >> n >> x;
    vector<int> a(n + 1);
    for(int i = 1; i <= n; i++) {
        cin >> a[i];
    }
    vector<int> dp(n + 1);
    dp[0] = 1;
    for(int i = 1; i <= n; i++) {
        for(int price = a[i]; i <= x; i++) {
            dp[i] += dp[price - a[i]];
            dp[i] %= (int) 1e9 + 7;
        }
    }
    cout << dp[x];
}
