int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    int n;
    cin >> n;
    vector<long long> dp(n + 1, 1e9);
    dp[0] = 0;
    for(int i = 1; i <= n; i++) {
        int num = i;
        while (num) {
            long long d = num % 10;
            dp[i] = min(dp[i], 1 + dp[i - d]);
            num /= 10;
        }
    }
    cout << dp[n];
}
