int main() {
    #define int long long
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    int n, m;
    cin >> n >> m;
    vector<int> weights(n + 1), values(n + 1);
    for(int i = 1; i <= n; i++) {
        int a, b;
        cin >> a >> b;
        weights[i] = a;
        values[i] = b;
    }
    vector<int> dp(m + 1);
    for(int i = 1; i <= m; i++) {
        for(int j = 1; j <= n; j++) {
            if(i >= weights[j])
            dp[i] = max(dp[i], dp[i - weights[j]] + values[j]);
        }
    }
    cout << dp[m];
}
