int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);

    int n;
    cin >> n;

    vector<pair<int, int>> points(n);
    unordered_map<int, int> freqX, freqY;

    for (int i = 0; i < n; ++i) {
        int x, y;
        cin >> x >> y;
        points[i] = {x, y};
        freqX[x]++;
        freqY[y]++;
    }

    long long result = 0;
    for (int i = 0; i < n; ++i) {
        int x = points[i].first;
        int y = points[i].second;
        result += (freqX[x] - 1) * (freqY[y] - 1);
    }

    cout << result << "\n";
    return 0;
}
