const int MOD = 1e9 + 7;
int main() {
    #define int long long
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    int n, m;
    cin >> n >> m;
    vector<vector<int>> dp(n + 1, vector<int>(n + 1));
    dp[0][0] = 1;
    for(int i = 1; i <= n; i++) {
        for(int j = 1; j <= n; j++) {
            dp[i][j] = (dp[i - 1][j] * j) % MOD + (dp[i - 1][j - 1] * (m - j + 1)) % MOD;
            dp[i][j] %= MOD;
        }
    }
    int ans = 0;
    for(int i = 1; i <= n; i++) {
        ans += dp[n][i] * i * i;
        ans %= MOD;
    }
    cout << ans;
}
