#pragma GCC optimize("trapv")
std::ostream&
operator<<( std::ostream& dest, __int128_t value )
{
    std::ostream::sentry s( dest );
    if ( s ) {
        __uint128_t tmp = value < 0 ? -value : value;
        char buffer[ 128 ];
        char* d = std::end( buffer );
        do
        {
            -- d;
            *d = "0123456789"[ tmp % 10 ];
            tmp /= 10;
        } while ( tmp != 0 );
        if ( value < 0 ) {
            -- d;
            *d = '-';
        }
        int len = std::end( buffer ) - d;
        if ( dest.rdbuf()->sputn( d, len ) != len ) {
            dest.setstate( std::ios_base::badbit );
        }
    }
    return dest;
}
//idk why, just look at python version
int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);

    string s;
    __int128_t n;
    long long tmp;
    cin >> s >> tmp;
    n = tmp;
    int len_s = s.size();

    vector<pair<__int128_t, __int128_t>> dp(len_s + 5, {0, 0});
    dp[len_s] = {-1, n};  

    for (int i = len_s - 1; i >= 0; --i) {
        if (s[i] == '0') {
            dp[i] = {1 + dp[i + 1].first, 0};  
        } else {
            dp[i] = {1e18, 0};
            __int128_t temp = 0;  

            for (int j = i; j < min(i + 20, len_s); ++j) {
                temp = temp * 10 + (s[j] - '0');  
                if (temp <= dp[j + 1].second) {
                    if (dp[i].first > 1 + dp[j + 1].first) {
                        dp[i] = {1 + dp[j + 1].first, temp};
                    } else if (dp[i].first == 1 + dp[j + 1].first) {
                        dp[i] = {1 + dp[j + 1].first, max(temp, dp[i].second)};
                    }
                }
            }
        }
    }

    if (dp[0].first < 1e18) {
        cout << dp[0].first << '\n';
    } else {
        cout << "NO WAY\n";
    }

    return 0;
}