int main() {
    int N, K;
    cin >> N >> K;
    
    vector<int> A(N);
    for(int i = 0; i < N; ++i) {
        cin >> A[i];
    }
    
    sort(A.begin(), A.end());
    
    int min_difference = INT_MAX;
    int window_size = N - K;
    for(int i = 0; i <= N - window_size; ++i) {
        int difference = A[i + window_size - 1] - A[i];
        if(difference < min_difference) {
            min_difference = difference;
        }
    }
    
    cout << min_difference << endl;
    return 0;
}