#define debugArr(...) std::cerr << __LINE__ << ": [", __DEBUG_UTIL__::printerArr(#__VA_ARGS__, __VA_ARGS__)
#else
#define debug(...)
#define debugArr(...)
#endif
const int MAX_N = 2002;

int n, m;
char grid[MAX_N][MAX_N];
int pref[MAX_N][MAX_N];
long long botl[MAX_N][MAX_N], botr[MAX_N][MAX_N];
int left_[MAX_N][MAX_N], lastl[MAX_N][MAX_N];

int query(int x1, int y1, int x2, int y2) {
    x1 = max(x1, 0);
    y1 = max(y1, 0);
    x2 = max(x2, 0);
    y2 = max(y2, 0);

    return pref[x2][y2] - pref[x1 - 1][y2] - pref[x2][y1 - 1] + pref[x1 - 1][y1 - 1];
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n >> m;

    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            cin >> grid[i][j];
        }
    }

    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            if (grid[i][j] == '#') {
                left_[i][j] = j;
                lastl[i][j] = lastl[i][j - 1];
            } else {
                left_[i][j] = left_[i][j - 1];
                lastl[i][j] = j;
            }
        }
    }

    long long result = 0;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            pref[i][j] = pref[i - 1][j] + pref[i][j - 1] - pref[i - 1][j - 1] + (grid[i][j] == '#');
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            if (grid[i][j] == '#') continue;

            if (i == 1) {
                botr[i][j] = j;
                botl[i][j] = left_[i][j];
            } else {
                botr[i][j] = j - query(i, 1, i, j) + botr[i - 1][lastl[i - 1][j]];
                botl[i][j] = left_[i][j] + botl[i - 1][left_[i][j] + 1] - query(i, 1, i, lastl[i][j]);
            }

            result += 1LL * (i - 1) * m + j - query(1, 1, i - 1, m) - query(i, 1, i, j) - botr[i][j] + botl[i][j];
        }
    }

    cout << result << '\n';
    return 0;
}
