using cd = complex<long double>;
// i give up on coding myself :\ please help
int reverse(int num, int lg_n) {
    int res = 0;
    for (int i = 0; i < lg_n; i++) {
        if (num & (1 << i))
            res |= 1 << (lg_n - 1 - i);
    }
    return res;
}

void fft(vector<cd> & a, bool invert) {
    int n = a.size();
    int lg_n = 0;
    while ((1 << lg_n) < n)
        lg_n++;

    for (int i = 0; i < n; i++) {
        if (i < reverse(i, lg_n))
            swap(a[i], a[reverse(i, lg_n)]);
    }

    for (int len = 2; len <= n; len <<= 1) {
        double ang = 2 * 3.14159265358979323846 / len * (invert ? -1 : 1);
        cd wlen(cos(ang), sin(ang));
        for (int i = 0; i < n; i += len) {
            cd w(1);
            for (int j = 0; j < len / 2; j++) {
                cd u = a[i+j], v = a[i+j+len/2] * w;
                a[i+j] = u + v;
                a[i+j+len/2] = u - v;
                w *= wlen;
            }
        }
    }

    if (invert) {
        for (cd & x : a)
            x /= n;
    }
}

vector<cd> multiply(vector<cd> const& a, vector<cd> const& b) {
    vector<cd> fa(a.begin(), a.end()), fb(b.begin(), b.end());
    int n = 1;
    while (n < a.size() + b.size()) 
        n <<= 1;
    fa.resize(n);
    fb.resize(n);

    fft(fa, false);
    fft(fb, false);
    for (int i = 0; i < n; i++)
        fa[i] *= fb[i];
    fft(fa, true);

    vector<cd> result(n);
    for (int i = 0; i < n; i++)
        result[i] = fa[i];
    return result;
}
void solve() {
    int n, m;
    cin >> n >> m;
    vector<cd> a(n), b(m);
    for(cd &i : a) {
        cin >> i;
    }
    for(cd &i : b) {
        cin >> i;
    }
    auto ans = multiply(a, b);
    for(auto i: ans) {
        if(abs(i) > 1e-7)
        cout << (long long)round(real(i) + imag(i)) % 998244353 << ' ';
    }
    cout << '\n';
}
vector<long long> solve(const string &s) {
    int n = s.size(), n_ = 1;
    while (n_ < 2*n) n_ <<= 1;

    vector<cd> a(n_), b(n_);

    for (int i = 0; i < n; ++i) {
        if (s[i] == 'B')        a[i] = 1;
        if (s[n-1-i] == 'A')    b[i] = 1;
    }

    fft(a, false);
    fft(b, false);
    for (int i = 0; i < n_; ++i)
        a[i] *= b[i];

    fft(a, true);

    vector<long long> count(n, 0);
    for (int d = 1; d < n; ++d) cout << (long long)round(a[n-1-d].real()) << '\n';
    return count;
}
int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    string s;
    cin >> s;
    solve(s);
}