int main() {
    int CU, DU, CD, DD, CP, DP, H;
    cin >> CU >> DU;
    cin >> CD >> DD;
    cin >> CP >> DP;
    cin >> H;

    int time = 0;
    int totalDamage = 0;

    while (H > 0) {
        if (time % CU == 0) {
            H -= DU;
        }
        if (time % CD == 0) {
            H -= DD;
        }
        if (time % CP == 0) {
            H -= DP;
        }
        time++;
    }

    cout << time - 1 << endl;
    return 0;
}
