int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);

    int n;
    cin >> n;
    vector<int> a(n);
    vector<int> mp(1e6 + 5);

    for(int i = 0; i < n; i++) {
        cin >> a[i];
        mp[a[i]] = i + 1;
    }
    
    int operations = 0;
    for(int i = 1; i <= n; i++) {
        int prev = i, next = i;
        while(prev <= n and mp[prev] < mp[prev + 1]) prev ++;
        while(next <= n and mp[next] > mp[next + 1]) next ++;
        operations++;
        i = max(prev, next);
        //cout << i << endl;
    }
    cout << operations << endl;

    return 0;
}