int main() {
    #define int long long
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    int n;
    cin >> n;
    vector<pair<int, int>> a(n);
    for(int i = 0; i < n; i++) {
        cin >> a[i].first >> a[i].second;
    }
    sort(a.begin(), a.end());
    vector<int> ls;
    for(auto i:a) {
        ls.push_back(i.first);
    }
    sort(ls.begin(), ls.end());
    long long cnt = 0;
    for(int i = 0; i < n; i++) {
        cnt += upper_bound(ls.begin() + i + 1, ls.end(), a[i].second) - ls.begin() - i - 1;
    }
    cout << cnt;
}
