#define int long long
constexpr int MAX_COORD = 2e6;       // Maximum coordinate value
constexpr int OFFSET = MAX_COORD + 2; // Offset for 0-based indexing
constexpr int ARRAY_SIZE = 3 * MAX_COORD + 5; // Total array size

int prefx[ARRAY_SIZE], prefy[ARRAY_SIZE];
int prefx1[ARRAY_SIZE], prefy1[ARRAY_SIZE];

int distsx(int i) {
    int lower = (i - OFFSET) * prefx1[i] - prefx[i];
    int upper = (prefx[ARRAY_SIZE - 1] - prefx[i]) - (i - OFFSET) * (prefx1[ARRAY_SIZE - 1] - prefx1[i]);
    return lower + upper;
}

int distsy(int i) {
    int lower = (i - OFFSET) * prefy1[i] - prefy[i];
    int upper = (prefy[ARRAY_SIZE - 1] - prefy[i]) - (i - OFFSET) * (prefy1[ARRAY_SIZE - 1] - prefy1[i]);
    return lower + upper;
}

signed main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);

    int n, k;
    cin >> n >> k;

    for (int i = 0; i < n; i++) {
        int x, y;
        cin >> x >> y;
        prefx1[x + OFFSET]++;
        prefy1[y + OFFSET]++;
        prefx[x + OFFSET] += x;
        prefy[y + OFFSET] += y;
    }

    for (int i = 1; i < ARRAY_SIZE; i++) {
        prefx[i] += prefx[i - 1];
        prefy[i] += prefy[i - 1];
        prefx1[i] += prefx1[i - 1];
        prefy1[i] += prefy1[i - 1];
    }

    vector<int> stor;
    for (int i = -MAX_COORD; i <= MAX_COORD; i++) {
        stor.push_back(distsy(i + OFFSET));
    }
    sort(stor.begin(), stor.end());
    int ans = 0;
    for (int i = -MAX_COORD; i <= MAX_COORD; i++) {
        if (distsx(i + OFFSET) <= k) {
            ans += upper_bound(stor.begin(), stor.end(), k - distsx(i + OFFSET)) - stor.begin();
        }
    }
    cout << ans << '\n';
    return 0;
}
