#define int long long
#pragma GCC optimize("Ofast,fast-math,unroll-loops")
#pragma GCC target("arch=alderlake")
const int MOD = 1e9 + 7;

int binpow(int a, int b, int m = MOD) {
    a %= m;
    int res = 1;
    while (b > 0) {
        if (b & 1)
            res = res * a % m;
        a = a * a % m;
        b >>= 1;
    }
    return res;
}

const int MAX_N = 1e6 + 5;
vector<int> prime;
bool is_prime[MAX_N + 5];
void sieve(int n = 1e6 + 2) {
    memset(is_prime, 1, sizeof(is_prime));
    is_prime[1] = false;

    for (int p = 2; p * p <= n; p++) {
        if (is_prime[p]) {
            for (int i = p * p; i <= n; i += p) {
                is_prime[i] = false;
            }
        }
    }

    for (int p = 2; p <= n; p++) {
        if (is_prime[p]) {
            prime.push_back(p);
        }
    }
}

vector<int> factor(int n) {
    vector<int> exponents;
    for (int p : prime) {
        if (p * p > n) break;
        int exp = 0;
        while (n % p == 0) {
            n /= p;
            exp++;
        }
        if (exp > 0) exponents.push_back(exp);
    }
    if (n > 1) exponents.push_back(1);
    return exponents;
}

int fact[MAX_N];
void calcfact(int lim = 1e6) {
    fact[0] = 1;
    for (int i = 1; i <= lim; i++) {
        fact[i] = fact[i - 1] * i % MOD;
    }
}

int comb(int n, int k) {
    if((k > n || k < 0)) return 0;
    return fact[n] * binpow(fact[k], MOD - 2) % MOD * binpow(fact[n - k], MOD - 2) % MOD;
}
mt19937 rng(chrono::steady_clock::now().time_since_epoch().count());
signed main() {
    sieve();
    calcfact();

    int q;
    //cin >> q;
    while (true) {
        int x, y;
        x = rng() % (int) 1e5 + 10;
        y = rng() % (int) 1e5 + 10;
        vector<int> exponents = factor(x);

        int result = 1;
        for (int exp : exponents) {
            result = result * comb(exp + y - 1, y - 1) % MOD;
        }
        if(result == 0) {
            cout << x << ' ' << y;
            exit(0);
        }
        cout << result * binpow(2, y - 1) % MOD << '\n';
    }
    return 0;
}