int main() {
    int n;
    cin >> n;
    vector<long long> potions(n);
    for (int i = 0; i < n; ++i) {
        cin >> potions[i];
    }

    long long current_health = 0;
    int count = 0;
    priority_queue<long long, vector<long long>, greater<long long>> min_heap;

    for (int i = 0; i < n; ++i) {
        if (current_health + potions[i] >= 0) {
            current_health += potions[i];
            min_heap.push(potions[i]);
            ++count;
        } else if (!min_heap.empty() && potions[i] > min_heap.top()) {
            current_health += potions[i] - min_heap.top();
            min_heap.pop();
            min_heap.push(potions[i]);
        }
    }

    cout << count << endl;
    return 0;
}
