int main() {
    int T;
    cin >> T;
    while (T--) {
        int A, B;
        cin >> A >> B;

        int additional_stems = 0, additional_leaves = 0;

        int min_additional = INT_MAX;

        for (int extra_stems = 0; extra_stems <= 3; ++extra_stems) {
            int total_stems = A + extra_stems;
            int total_leaves = B;
            if (total_stems * 3 >= total_leaves && (total_stems * 3 - total_leaves) % 1 == 0) {

                min_additional = min(min_additional, extra_stems + (total_stems * 3 - total_leaves));
            }
            if (total_stems * 4 >= total_leaves && (total_stems * 4 - total_leaves) % 1 == 0) {

                min_additional = min(min_additional, extra_stems + (total_stems * 4 - total_leaves));
            }
        }

        cout << min_additional << endl;
    }
    return 0;
}