#define int long long
const int maxn = 505;
const int mod = 1e9 + 7;
int f[maxn][maxn][3], power[maxn];

string n, m;
long long binpow(long long base, int exp) {
    long long res = 1;
    while (exp) {
        if (exp & 1) res *= base;
        base *= base;
        base %= mod;
        res %= mod;
        exp >>= 1;
    }
    return res;
}
signed main() {
    cin >> n >> m;
    if(n.size() < m.size()) {
        cout << binpow(2, n.size() - 1);
        return 0;
    }
    while(m.size() < n.size()) m = "0" + m;
    int a = n.size();
    n = " " + n;
    m = " " + m;
    for(int i = 1; i <= a; i++) {
        f[i][i][0] = n[1] < m[i];
        f[i][i][1] = n[1] == m[i];
        f[i][i][2] = n[1] > m[i];
    }
    for(int i = 2; i <= a; i++) {
        for(int j = 1; j + i - 1 <= a; j++) {
            int l = j, r = j + i - 1;
            if(n[i] < m[l]) {
                f[l][r][0] = (
                    f[l][r][0] +
                    f[l + 1][r][0] +
                    f[l + 1][r][1] + 
                    f[l + 1][r][2]
                ) % mod;
            } else if(n[i] == m[l]) {
                for(int t = 0; t <= 2; t++) {
                    f[l][r][t] += f[l + 1][r][t];
                    f[l][r][t] %= mod;
                }
            } else {
                f[l][r][2] = (f[l][r][2] + f[l + 1][r][0] + f[l + 1][r][1] + f[l + 1][r][2]) % mod;
            }
            if(n[i] > m[r]) {
                f[l][r][0] = (f[l][r][0] + f[l][r - 1][0]) % mod;
                f[l][r][2] = (f[l][r][2] + f[l][r - 1][1] + f[l][r - 1][2]) % mod;
            } else if(n[i] == m[r]) {
                for(int t = 0; t <= 2; t++) {
                    f[l][r][t] += f[l][r - 1][t];
                    f[l][r][t] %= mod;
                }
            } else {
                f[l][r][2] = f[l][r][2] + f[l][r - 1][2];
                f[l][r][2] %= mod;
                f[l][r][0] = f[l][r][0] + f[l][r - 1][0] + f[l][r - 1][1];
                f[l][r][0] %= mod;
            }
        }
    }
    cout << (f[1][a][0] + f[1][a][1]) % mod;
}