#define debug(...)
#define debugArr(...)
#endif
const long long MOD = 1e9 + 7;
const long long BASE = 373;

string s, t;
vector<long long> powmod, hashs, revhash;

long long get_hash(int l, int r) {
    long long res = (hashs[r + 1] - hashs[l] * powmod[r - l + 1]) % MOD;
    return (res + MOD) % MOD;
}

long long get_revhash(int l, int r) {
    long long res = (revhash[l] - revhash[r + 1] * powmod[r - l + 1]) % MOD;
    return (res + MOD) % MOD;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);

    cin >> s >> t;

    int total0 = count(s.begin(), s.end(), '0');
    int total1 = count(s.begin(), s.end(), '1');

    int m = t.size();
    powmod.assign(m + 1, 1);
    for (int i = 1; i <= m; i++)
        powmod[i] = (powmod[i - 1] * BASE) % MOD;

    hashs.assign(m + 1, 0);
    for (int i = 0; i < m; i++)
        hashs[i + 1] = (hashs[i] * BASE + t[i]) % MOD;

    revhash.assign(m + 2, 0);
    for (int i = m - 1; i >= 0; i--)
        revhash[i] = (revhash[i + 1] * BASE + t[i]) % MOD;

    int border = 0;
    for (int len = 1; len < m; len++) {
        if (get_hash(0, len - 1) == get_hash(m - len, m - 1)) {
            border = len;
        }
    }

    int need0 = count(t.begin(), t.end(), '0');
    int need1 = count(t.begin(), t.end(), '1');

    int suff0 = count(t.begin() + border, t.end(), '0');
    int suff1 = count(t.begin() + border, t.end(), '1');

    string ans;

    if (total0 >= need0 && total1 >= need1) {
        ans += t;
        total0 -= need0;
        total1 -= need1;

        while (total0 >= suff0 && total1 >= suff1) {
            ans += t.substr(border);
            total0 -= suff0;
            total1 -= suff1;
        }
    }

    ans += string(total0, '0') + string(total1, '1');
    cout << ans << '\n';
}
