int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    int q;
    cin >> q;
    while(q--) {
        int n, p, q;
        cin >> n;
        cin >> p >> q;
        vector<long long> v(n);
        for (long i = 0; i < n; i++)
            cin >> v[i];
        sort(v.begin(), v.end());

        long long ans = v[n - 1] - v[0];
        vector<long long> x;
        for (long i = 1; i < n - 1; i++)
            x.push_back(abs(v[i]));
        sort(x.rbegin(), x.rend());
        long sz = min((int) x.size(), p + q);
        for (long i = 0; i < sz; i++)
            ans += x[i];
        cout << ans << endl;
    }
}
