const int population = 100;
const int survive = 10;
int state = rand();
inline int coin() {
    int x = state;
    state >>= 1;
    state  ^= x;
    return (((bool) (state % 2)) ? -1 : 1) ;
}
vector<long double> evolute(vector<long double> pop, long double temp) {
    for(long double &i : pop) {
        i += coin() * temp;
    }
    return pop;
}
const long double eps = 1e-9;
struct polynomial {
    long double a, b, c, d;
    inline long double calc(long double x) {
        return a * pow(x, 3) + b * pow(x, 2) + c * x + d;
    }
};
polynomial p;
bool check(vector<long double> pop) {
    for(long double i : pop) {
        if(abs(p.calc(i)) <= eps) {
            cout <<fixed << setprecision(11) << i;
            return 1;
        }
    }
    return 0;
}
bool comp(long double x, long double y) {
    return abs(p.calc(x)) < abs(p.calc(y));
}
vector<long double> killandrepop(vector<long double> pop) {
    sort(pop.begin(), pop.end(), comp);
    vector<long double> newpop;
    for(int i = 0; i < min((int) pop.size(), survive); i++) {
        newpop.push_back(pop[i]);
    }
    int it = 0;
    while(newpop.size() < population) {
        it++;
        it %=  min((int) pop.size(), survive);
        newpop.push_back(pop[it]);
    }
    return newpop;
}
vector<long double> proc(vector<long double> x) {
    for(auto &i : x) {
        i = p.calc(i);
    }    
    return x;
}
long double cand(vector<long double> pop) {
    long double ans = abs(pop[0]);
    for(long double x:pop) {
        ans = min(ans, abs(p.calc(x)));
    }
    return ans;
}
int main() {
    
    cin >> p.a >> p.b >> p.c >> p.d;
    vector<long double> popu;
    long double tmp = -1e9;
    for(long double i = -population; i <= population; i += 1) {
        popu.push_back(tmp);
        tmp += 1e7;
    }
    long double temp = 1000;
    while(true) {
        if(check(popu)) break;
        popu = evolute(popu, temp);
        popu = killandrepop(popu);
        temp /= 1.115;
    }
}