using namespace std;

static const int MAXN = 5005;
static const int MOD = 1000000007;

alignas(32) int C_flat[(MAXN+1) * (MAXN+1)];
int* C[MAXN+1];

inline int comb(int n, int k) {
    return C_flat[n * (MAXN+1) + k];
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    for (int i = 0; i <= MAXN; ++i) {
        C[i] = C_flat + i * (MAXN+1);
    }
    memset(C_flat, 0, sizeof(C_flat));
    C[0][0] = 1;
    for (int n = 1; n <= MAXN; ++n) {
        C[n][0] = C[n][n] = 1;
        for (int k = 1; k < n; ++k) {
            C[n][k] = (C[n-1][k-1] + C[n-1][k]) % MOD;
        }
    }

    int T;
    cin >> T;
    while (T--) {
        int n, q;
        cin >> n >> q;
        string a;
        cin >> a;

        const int p1 = 31, m1 = 1000000009;
        const int p2 = 37, m2 = 1000000007;
        vector<pair<int,int>> hashes;
        hashes.reserve((long long)n * (n+1) / 2);

        for (int i = 0; i < n; ++i) {
            long long h1 = 0, h2 = 0;
            for (int j = i; j < n; ++j) {
                h1 = (h1 * p1 + (a[j] - 'a' + 1)) % m1;
                h2 = (h2 * p2 + (a[j] - 'a' + 1)) % m2;
                hashes.emplace_back((int)h1, (int)h2);
            }
        }

        sort(hashes.begin(), hashes.end());

        vector<int> freq_counts(n+1);
        fill(freq_counts.begin(), freq_counts.end(), 0);
        long long run = 1;
        for (size_t i = 1; i <= hashes.size(); ++i) {
            if (i < hashes.size() && hashes[i] == hashes[i-1]) {
                ++run;
            } else {
                if (run <= n) freq_counts[run]++;
                run = 1;
            }
        }

        vector<long long> ans(n+1);
        const int stride = MAXN + 1;
        __m128i incr = _mm_set_epi32(3*stride, 2*stride, 1*stride, 0);

        for (int j = 1; j <= n; ++j) {
            long long total = 0;
            int k = j;
            for (; k + 3 <= n; k += 4) {
                __m128i freq4 = _mm_loadu_si128((__m128i*)&freq_counts[k]);
                __m256i freq64 = _mm256_cvtepi32_epi64(freq4);
                int base = k * stride + j;
                __m128i base_vec = _mm_set1_epi32(base);
                __m128i idx_vec = _mm_add_epi32(base_vec, incr);
                __m128i comb32 = _mm_i32gather_epi32(C_flat, idx_vec, 4);
                __m256i comb64 = _mm256_cvtepi32_epi64(comb32);
                __m256i prod = _mm256_mul_epi32(freq64, comb64);
                long long part[4];
                _mm256_storeu_si256((__m256i*)part, prod);
                total = (total + (part[0] + part[1] + part[2] + part[3]) % MOD) % MOD;
            }
            for (; k <= n; ++k) {
                if (freq_counts[k] > 0) {
                    total = (total + 1LL * freq_counts[k] * comb(k, j)) % MOD;
                }
            }
            ans[j] = total;
        }

        while (q--) {
            int x;
            cin >> x;
            cout << (x <= n ? ans[x] : 0) << '\n';
        }
    }
}