constexpr int population_size = 100;
constexpr int survivors = 10;
constexpr long double eps = 1e-10L;
constexpr int max_iters = 300;

mt19937_64 rng(chrono::steady_clock::now().time_since_epoch().count());
uniform_int_distribution<int> coin_flip(0, 1);

struct Polynomial {
    long double a, b, c, d;
    long double eval(long double x) const {
        return ((a * x + b) * x + c) * x + d;
    }
} p;

long double derivative(long double x) {
    return (3 * p.a * x + 2 * p.b) * x + p.c;
}

long double polish_root(long double x) {
    for (int iter = 0; iter < 5; ++iter) {
        long double fx = p.eval(x);
        long double dfx = derivative(x);
        if (fabsl(dfx) < 1e-12L) break; 
        x -= fx / dfx;
    }
    return x;
}

void evolute_in_place(vector<long double> &pop, long double temp) {
    for (auto &x : pop) {
        x += (coin_flip(rng) ? 1 : -1) * temp;
    }
}

bool better(long double x, long double y) {
    return fabsl(p.eval(x)) < fabsl(p.eval(y));
}

void kill_and_repopulate(vector<long double> &pop) {
    sort(pop.begin(), pop.end(), better);
    pop.resize(survivors);
    while ((int)pop.size() < population_size) {
        pop.push_back(pop[pop.size() % survivors]);
    }
}

bool check_and_print(const vector<long double> &pop) {
    for (auto x : pop) {
        if (fabsl(p.eval(x)) <= 1e-6L) { 
            x = polish_root(x);
            if (fabsl(p.eval(x)) <= eps) { 
                cout << fixed << setprecision(10) << x << '\n'; 
                return true;
            }
        }
    }
    return false;
}

int main() {

    cin >> p.a >> p.b >> p.c >> p.d;

    long double R = 1e9L;

    vector<long double> pop;
    long double step = (2 * R) / (population_size - 1);
    for (int i = 0; i < population_size; ++i) {
        pop.push_back(-R + i * step);
    }

    long double temp = R;
    long double alpha = powl(eps / temp, 1.0L / max_iters);

    for (int iter = 0; iter < max_iters; ++iter) {
        evolute_in_place(pop, temp);
        kill_and_repopulate(pop);
        if (check_and_print(pop)) return 0;
        temp *= alpha;
    }
}