int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    int n, x;
    cin >> n >> x;
    vector<int> a(n + 1);
    for(int i = 1; i <= n; i++){
        cin >> a[i];
    }
    map<int, pair<int, int>> mp;
    for(int i = 1; i <= n; i++) {
        for(int j = i + 1; j <= n; j++) {
            mp[a[i] + a[j]] = {i, j};
        }
    }
    for(int i = 1; i <= n; i++) {
        for(int j = i + 1; j <= n; j++) {
            if(mp[x - a[i] - a[j]] != make_pair(0, 0) and mp[x - a[i] - a[j]].first > j) {
                cout << i << ' ' << j << ' ' << mp[x - a[i] - a[j]].first << ' ' << mp[x - a[i] - a[j]].second;
                return 0;
            }
        }
    }
    cout << "IMPOSSIBLE";
}
