const int dx[] = {-1, 1, 0, 0};
const int dy[] = {0, 0, -1, 1};
const int maxn = 1e3 + 5;
const int maxel = 4e6 + 5;
int pos[maxn][maxn];
int a[maxn][maxn];

int m, n;
bool checkind(int u, int v) {
    return 1 <= u and u <= m and 1 <= v and v <= n;
}
void dfs(int u, int v, int id) {
    if(pos[u][v] != 0) return;

    pos[u][v] = id;
    for(int d = 0; d < 4; d++) {
        int x = u + dx[d];
        int y = v + dy[d];
        if(checkind(x, y) and a[x][y] == a[u][v]) {
            dfs(x, y, id);
        }
    }
}
vector<pair<int, int>> adj; // edge from first -> second
vector<int> color[maxel]; // color[i]: ids with color i
int colorid[maxel]; //color of index i
int par[maxel], size_[maxel];
int par_[maxel], size__[maxel];
int getpar(int i) {
    while(par[i] != i) i = par[i];
    return i;
}
int ans = 0;
void join(int a, int b) {
    a = getpar(a);
    b = getpar(b);
    if(a == b) return; // :D
    if(size_[a] > size_[b]) swap(a, b);
    par[a] = b;
    size_[b] += size_[a];
    ans = max(ans, size_[b]);
}
int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cin >> m >> n;
    for (int i = 1; i <= m; i++) {
        for (int j = 1; j <= n; j++) {
            cin >> a[i][j];
        }
    }
    int id = 1;
    for (int i = 1; i <= m; i++) {
        for (int j = 1; j <= n; j++) {
            if(pos[i][j] == 0) {
                dfs(i, j, id);
                colorid[id] = a[i][j];
                color[a[i][j]].push_back(id);
                id++;
            }
        }
    }
    
    for (int i = 1; i <= m; i++) {
        for (int j = 1; j <= n; j++) {
            for(int d = 0; d < 4; d++) {
                int x = i + dx[d];
                int y = j + dy[d];
                if(checkind(x, y) and a[x][y] != a[i][j] and a[x][y] < a[i][j]) {
                    adj.push_back({pos[x][y], pos[i][j]});
                }
            }
            size_[pos[i][j]]++;
        }
    }
    for(int i = 1; i < id; i++) {
        par[i] = i;
        ans = max(ans, size_[i]);
        par_[i] = par[i];
        size__[i] = size_[i];
    }
    sort(adj.begin(), adj.end(), [](pair<int, int> a, pair<int, int> b) {
        return tie(colorid[a.first], colorid[a.second]) < tie(colorid[b.first], colorid[b.second]);
    });
    for(int ad = 0; ad < adj.size(); ad++) {
        int prev = ad;
        while(prev < adj.size()
        and
        colorid[adj[prev].first] == colorid[adj[ad].first]
        and
        colorid[adj[prev].second] == colorid[adj[ad].second]
        ) prev++;
        prev--;
        for(int i = ad; i <= prev; i++) {
            join(adj[i].first, adj[i].second);
        }
        for(int ind = ad; ind <= prev; ind++) {
            int x, y;
            x = adj[ind].first;
            y = adj[ind].second;
            par[x] = par_[x];
            par[y] = par_[y];
            size_[x] = size__[x];
            size_[y] = size__[y];
        }
        ad = prev;
    }
    cout << ans << '\n';
    return 0;
}