typedef long long ll;
const ll INF = 1e9;

struct Query {
    int l, r, idx;
};

struct PairInfo {
    int l, r, diff; 
};

struct SegmentTree {
    int n;
    vector<ll> tree;

    SegmentTree(int n) : n(n) {
        tree.assign(4*n, INF);
    }

    void update(int idx, int l, int r, int pos, ll val) {
        if(l == r) {
            tree[idx] = min(tree[idx], val);
            return;
        }
        int mid = (l+r) / 2;
        if(pos <= mid) update(2*idx, l, mid, pos, val);
        else update(2*idx+1, mid+1, r, pos, val);
        tree[idx] = min(tree[2*idx], tree[2*idx+1]);
    }

    ll query(int idx, int l, int r, int ql, int qr) {
        if(ql > r || qr < l) return INF;
        if(ql <= l && r <= qr) return tree[idx];
        int mid = (l+r)/2;
        return min(query(2*idx, l, mid, ql, qr),
                   query(2*idx+1, mid+1, r, ql, qr));
    }
};

int main(){
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m;
    cin >> n >> m;
    vector<int> a(n+1);
    for(int i=1; i<=n; i++){
        cin >> a[i];
    }
    map<int, vector<int>> pos;
    for (int i = 1; i <= n; i++){
        pos[a[i]].push_back(i);
    }

    vector<PairInfo> pairs;
    for (pair<int, vector<int>> t : pos){
        int color = t.first;
        if(pos[color].size() >= 2){
            for (size_t i = 0; i+1 < pos[color].size(); i++){
                int p1 = pos[color][i];
                int p2 = pos[color][i+1];
                pairs.push_back({p1, p2, p2 - p1});
            }
        }
    }

    sort(pairs.begin(), pairs.end(), [](const PairInfo &a, const PairInfo &b){
        return a.r < b.r;
    });

    vector<Query> queries(m);
    for (int i = 0; i < m; i++){
        int l, r;
        cin >> l >> r;
        queries[i] = {l, r, i};
    }
    sort(queries.begin(), queries.end(), [](const Query &a, const Query &b){
        return a.r < b.r;
    });

    SegmentTree segt(n);
    vector<ll> ans(m, -1);
    int ptr = 0; 
    for(auto &q: queries) {

        while(ptr < pairs.size() && pairs[ptr].r <= q.r){
            segt.update(1, 1, n, pairs[ptr].l, pairs[ptr].diff);
            ptr++;
        }
        ll cur = segt.query(1, 1, n, q.l, q.r);
        if(cur == INF) ans[q.idx] = -1;
        else ans[q.idx] = cur;
    }

    for(auto x: ans)
        cout << x << "\n";

    return 0;
}