void permu(string &str, int l, int r, set<string> &results) {
    if (l == r) {
        results.insert(str);
    } else {
        for (int i = l; i <= r; ++i) {
            swap(str[l], str[i]);
            permu(str, l + 1, r, results);
            swap(str[l], str[i]);
        }
    }
}

int main() {
    string s;
    cin >> s;

    set<string> results;
    permu(s, 0, s.length() - 1, results);

    cout << results.size() << endl;

    for (const auto &perm : results) {
        cout << perm << endl;
    }

    return 0;
}
