int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    int n;
    cin >> n;
    vector<int> a(n + 1);
    map<int, int> mp;
    for(int i = 1; i <= n; i++) {
        cin >> a[i];
        mp[a[i]] = i;
    }
    vector<int> ans(n + 1);
    for(int i = 1; i <= n; i++) {
        for(int j = 1; j < mp[i]; j++) {
            if(a[j] >= i) ans[i] ++;
        }
    }
    for(int i = 1; i <= n; i++) {
        cout << ans[i] << ' ';
    }
}
