int main() {
    int n, k;
    cin >> n >> k;
    long long ans = 0;
    deque<pair<int, int>> dq;
    int c;
    cin >> c;
    dq.push_back({k, c});
    for(int i = 1; i <= n; i++) {
        
        ans += dq.front().second;
        if(--dq.front().first == 0) dq.pop_front();
        if(i == n) break;
        cin >> c;
        int dist = 1;
        while(!dq.empty() and dq.back().second > c) {
            dist += dq.back().first;
            dq.pop_back();
        }
        dq.push_back({dist, c});
        //cout << ans << endl;
    }
    cout << ans;
}
