#else
#define debug(...)
#define debugArr(...)
#endif
const long long NEG_INF = LLONG_MIN / 4;

int main(){
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m;
    cin >> n >> m;
    vector<vector<long long>> a(m, vector<long long>(n));
    for(int i = 0; i < n; i++)
        for(int j = 0; j < m; j++)
            cin >> a[j][i];

    int M = 1 << n;

    vector<vector<long long>> col_w(m, vector<long long>(M, 0));
    for(int j = 0; j < m; j++){
        for(int mask = 0; mask < M; mask++){
            long long s = 0;
            for(int r = 0; r < n; r++){
                if(mask & (1<<r)) 
                    s += a[j][r];
            }
            col_w[j][mask] = s;
        }
    }

    static bool ok1[32][32], ok2[32][32];
    for(int x = 0; x < M; x++){
        for(int y = 0; y < M; y++){
            bool conflict1 = false, conflict2 = false;

            if( ((x<<2) & y) || ((y<<2) & x) ) conflict1 = true;

            if( ((x<<1) & y) || ((y<<1) & x) ) conflict2 = true;
            ok1[x][y] = !conflict1;
            ok2[x][y] = !conflict2;
        }
    }

    static long long dp_prev[32][32], dp_curr[32][32];

    for(int i = 0; i < M; i++)
        for(int j = 0; j < M; j++)
            dp_prev[i][j] = NEG_INF;

    dp_prev[0][0] = 0;

    for(int col = 0; col < m; col++){

        for(int i = 0; i < M; i++)
            for(int j = 0; j < M; j++)
                dp_curr[i][j] = NEG_INF;

        for(int mask_pp = 0; mask_pp < M; mask_pp++){
            for(int mask_p = 0; mask_p < M; mask_p++){
                long long base = dp_prev[mask_pp][mask_p];
                if(base <= NEG_INF/2) continue;
                for(int mask = 0; mask < M; mask++){
                    if(!ok1[mask_p][mask]) continue;
                    if(!ok2[mask_pp][mask]) continue;
                    long long cand = base + col_w[col][mask];
                    dp_curr[mask_p][mask] = max(dp_curr[mask_p][mask], cand);
                }
            }
        }

        for(int i = 0; i < M; i++)
            for(int j = 0; j < M; j++)
                dp_prev[i][j] = dp_curr[i][j];
    }

    long long ans = 0;
    for(int i = 0; i < M; i++)
        for(int j = 0; j < M; j++)
            ans = max(ans, dp_prev[i][j]);

    cout << ans << "\n";
}