#pragma GCC optimize("trapv")
const int MAXN = 1000;
const int INF = 1e6 + 5;
int m, n, k;
int a[MAXN][MAXN], b[MAXN][MAXN];
int dx[] = {1, -1, 0, 0};
int dy[] = {0, 0, 1, -1};

void globalFloodFill() {
    queue<pair<pair<int, int>, pair<int, int>>> q;

    for (int i = 0; i < m; ++i) {
        for (int j = 0; j < n; ++j) {
            q.push({{i, j}, {k, a[i][j]}});
            b[i][j] = a[i][j];
        }
    }

    while (!q.empty()) {
        auto current = q.front();
        q.pop();

        int x = current.first.first;
        int y = current.first.second;
        int remainingSteps = current.second.first;
        int strength = current.second.second;

        for (int i = 0; i < 4; ++i) {
            int nx = x + dx[i];
            int ny = y + dy[i];
            if (nx >= 0 && nx < m && ny >= 0 && ny < n && remainingSteps > 0) {

                if (strength > b[nx][ny]) {
                    b[nx][ny] = strength;
                    q.push({{nx, ny}, {remainingSteps - 1, strength}});
                }
            }
        }
    }
}

void solve() {
    cin >> m >> n >> k;
    for (int i = 0; i < m; ++i)
        for (int j = 0; j < n; ++j)
            cin >> a[i][j];

    memset(b, 0, sizeof(b));
    globalFloodFill();

    for (int i = 0; i < m; ++i) {
        for (int j = 0; j < n; ++j) {
            cout << b[i][j] << " ";
        }
        cout << "\n";
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    solve();
    return 0;
}
