#include <bits/stdc++.h>
using namespace std;

#define int long long
const long long mod1 = 1e9 + 7;
const long long mod2 = 998244353;
const long long base1 = 373;
const long long base2 = 491;

long long hashs1[(int)1e6 + 5], hashs2[(int)1e6 + 5];
long long revhash1[(int)1e6 + 5], revhash2[(int)1e6 + 5];
long long powmod1[(int)1e6 + 5], powmod2[(int)1e6 + 5];
string S;
long long n;

void calcpow() {
    powmod1[0] = powmod2[0] = 1;
    for (long long i = 1; i <= n; i++) {
        powmod1[i] = (powmod1[i - 1] * base1) % mod1;
        powmod2[i] = (powmod2[i - 1] * base2) % mod2;
    }
}

void calchash() {
    for (long long i = 1; i <= n; i++) {
        hashs1[i] = (hashs1[i - 1] * base1 + S[i - 1]) % mod1;
        hashs2[i] = (hashs2[i - 1] * base2 + S[i - 1]) % mod2;
    }
    for (long long i = n; i >= 1; i--) {
        revhash1[i] = (revhash1[i + 1] * base1 + S[i - 1]) % mod1;
        revhash2[i] = (revhash2[i + 1] * base2 + S[i - 1]) % mod2;
    }
}

pair<long long, long long> getpre(int l, int r) {
    long long hash_value1 = (hashs1[r] - hashs1[l - 1] * powmod1[r - l + 1] % mod1 + mod1) % mod1;
    long long hash_value2 = (hashs2[r] - hashs2[l - 1] * powmod2[r - l + 1] % mod2 + mod2) % mod2;
    return {hash_value1, hash_value2};
}

pair<long long, long long> getsuf(int l, int r) {
    long long hash_value1 = (revhash1[l] - revhash1[r + 1] * powmod1[r - l + 1] % mod1 + mod1) % mod1;
    long long hash_value2 = (revhash2[l] - revhash2[r + 1] * powmod2[r - l + 1] % mod2 + mod2) % mod2;
    return {hash_value1, hash_value2};
}

string print(int l, int r) {
    return S.substr(l, r - l + 1);
}

signed main() {
    cin >> S;
    n = S.size();
    calcpow();
    calchash();
    for (int i = n / 2 + 1; i <= n; i++) {
        int r = n;
        int m = i;
        auto hasr = getpre(m, r);
        int len = r - m + 1;
        int l = i - len + 1;
        auto hasl = getsuf(l, m);
        if (hasr == hasl) {
            string left = print(0, m - 1);
            string righ = left;
            left.pop_back();
            reverse(righ.begin(), righ.end());
            cout << (left + righ);
            return 0;
        }
    }
    return 1;
}
