struct Fenwick {
    int n;
    vector<int> f;
    Fenwick(int _n): n(_n), f(n+1, 0) {}

    void update(int i, int v = 1) {
        for (; i <= n; i += i & -i)
            f[i] += v;
    }

    int query(int i) const {
        int s = 0;
        for (; i > 0; i -= i & -i)
            s += f[i];
        return s;
    }

    int query(int l, int r) const {
        return query(r) - query(l - 1);
    }
};

int main(){
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;
    vector<pair<int,int>> a(n);
    for(int i = 0; i < n; i++){
        cin >> a[i].first >> a[i].second;
    }

    int ax, ay, bx, by, q;
    cin >> ax >> ay >> bx >> by >> q;

    struct Query { long long r1sq, r2sq; int idx; };
    vector<Query> queries(q);
    for(int i = 0; i < q; i++){
        long long R1, R2;
        cin >> R1 >> R2;
        queries[i] = { R1*R1, R2*R2, i };
    }

    vector<pair<long long,long long>> pts(n);
    vector<long long> all_d2;
    all_d2.reserve(n);
    for(int i = 0; i < n; i++){
        long long dx1 = a[i].first  - ax;
        long long dy1 = a[i].second - ay;
        long long d1 = dx1*dx1 + dy1*dy1;

        long long dx2 = a[i].first  - bx;
        long long dy2 = a[i].second - by;
        long long d2 = dx2*dx2 + dy2*dy2;

        pts[i] = { d1, d2 };
        all_d2.push_back(d2);
    }

    sort(all_d2.begin(), all_d2.end());
    all_d2.erase(unique(all_d2.begin(), all_d2.end()), all_d2.end());

    sort(pts.begin(), pts.end(),
         [](auto &A, auto &B){ return A.first > B.first; });

    sort(queries.begin(), queries.end(),
         [](auto &A, auto &B){ return A.r1sq > B.r1sq; });

    Fenwick bit((int)all_d2.size());
    vector<int> ans(q);
    int p = 0;  

    for (auto &qr : queries) {

        while (p < n && pts[p].first > qr.r1sq) {
            int comp_idx = int(
              lower_bound(all_d2.begin(), all_d2.end(), pts[p].second)
              - all_d2.begin()
            );
            bit.update(comp_idx + 1, 1);
            p++;
        }

        int up = int(upper_bound(all_d2.begin(), all_d2.end(), qr.r2sq)
                     - all_d2.begin());
        int comp_count = 0;
        if (up < (int)all_d2.size()) {

            comp_count = bit.query(up+1, (int)all_d2.size());
        }

        ans[qr.idx] = n - comp_count;
    }

    for (int i = 0; i < q; i++) {
        cout << ans[i] << "\n";
    }
    return 0;
}