#endif
#define int long long
const int MOD = 1e9 + 7;
const int N = 205;
int dp[N][N][N], dp1[N][N][N];
signed main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    int n, k;
    cin >> n >> k;
    if(n == 1) {
        if(k == 1) {
            cout << 1;
        } else 
        cout << 0;
        return 0;
    }
    vector<int> b, w;
    bool odd = 0;
    for (int i = 1; i <= n; ++i) {
        odd ^= 1;
        int x = n, y = i, cnt = 0;
        while (x > 0 && y > 0) {
            --x;
            --y;
            ++cnt;
        }
        if (odd) b.push_back(cnt);
        else w.push_back(cnt);
    }
    for (int i = n - 1; i >= 1; --i) {
        odd ^= 1;
        int x = i, y = n, cnt = 0;
        while (x > 0 && y > 0) {
            --x;
            --y;
            ++cnt;
        }
        if (odd) b.push_back(cnt);
        else w.push_back(cnt);
    }
    for(int d = 0; d <= 1; d++) {
        for(int j = 0; j <= k; j++) {
            for(int x = 0; x <= k; x++) {
                if(x == 0) {
                    dp[(b.size() - 1) / 2 + 1][j][x] = 1;
                }
            }
        }
        for(int i = (b.size() - 1) / 2; i >= 0; i--) {
            for(int j = 0; j <= k; j++) {
                for(int x = 0; x <= k; x++) {
                    dp[i][j][x] = dp[i + 1][j][x];
                    
                    if(i != (int) b.size() - i - 1) {
                        if(x >= 1)
                        dp[i][j][x] += (2 * dp[i + 1][j + 1][x - 1] * (b[i] - j)) % MOD;
                        if(x >= 2)
                        dp[i][j][x] += dp[i + 1][j + 2][x - 2] * ((b[i] - j) * (b[i] - j - 1)) % MOD; // Take both i and b.size() - 1 - i
                    } else {
                        if(x >= 1)
                        dp[i][j][x] += (dp[i + 1][j + 1][x - 1] * (b[i] - j)) % MOD;
                    }
                    
                    dp[i][j][x] %= MOD;
                }
            }
        }
        swap(dp, dp1);
        swap(b, w);
    }
    int result = 0;
    for (int j = 0; j <= k; ++j) {
        result += (dp[0][0][j] * dp1[0][0][k - j]) % MOD;
        result %= MOD;
    }
    cout << result << "\n";
    return 0;
}