int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    int n, m;
    cin >> n >> m;
    vector<int> w(n + 1), v(n + 1);
    vector<int> dp(m + 1);
    for(int i = 1; i <= n; i++) {
        cin >> w[i] >> v[i];
    }
    for(int j = 0; j <= m; j++) {
        for(int i = 1; i <= n; i++) {
            if(j >= w[i]) {
                dp[j] = max(dp[j], dp[j - w[i]] + v[i]);
            }
        }
    }
    cout << dp[m];
}
