#define int long long
string s;
int n;
long long dp[(int) 1e3 + 5][(int) 1e3 + 5];
int recur(int l, int r) {
    if((r + 1) % n == l) {
        return 0;
    }
    if(dp[l][r] != LLONG_MIN) {
        return dp[l][r];
    }
    return dp[l][r] = max(-recur((l - 1 + n) % n, r) + (s[(l - 1 + n) % n] == 'D'), -recur(l, (r + 1) % n) + (s[(r + 1) % n] == 'D'));
}
signed main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    cin >> s;
    n = s.size();
    for(int i = 0; i <= 1e3 + 4; i++) {
        for(int j = 0; j <= 1e3 + 4; j++) {
            dp[i][j] = LLONG_MIN;
        }
    }
    int ans = 0;
    for(int r = 0; r < n; r++) {
        ans += (-recur(r, r) + (s[r] == 'D') > 0);
    }
    cout << ans;
}
