#pragma GCC optimize("trapv")
using ll = long long;
const int MOD1 = 1e9 + 7, MOD2 = 1e9 + 9;
const int BASE1 = 31, BASE2 = 997;

vector<ll> power1, power2, hash1, hash2;
pair<long long, long long> hashstring(const std::string &s) {
    long long hash1 = 0, hash2 = 0;
    long long power1 = 1, power2 = 1;

    for (char c : s) {
        hash1 = (hash1 * BASE1 + (c - 'a' + 1)) % MOD1;
        hash2 = (hash2 * BASE2 + (c - 'a' + 1)) % MOD2;
    }

    return {hash1, hash2};
}
void precompute_powers(int n) {
    power1.resize(n + 1, 1);
    power2.resize(n + 1, 1);
    for (int i = 1; i <= n; ++i) {
        power1[i] = power1[i - 1] * BASE1 % MOD1;
        power2[i] = power2[i - 1] * BASE2 % MOD2;
    }
}

void compute_hashes(const string &s) {
    int n = s.size();
    hash1.resize(n + 1, 0);
    hash2.resize(n + 1, 0);
    for (int i = 1; i <= n; ++i) {
        hash1[i] = (hash1[i - 1] * BASE1 + (s[i - 1] - 'a' + 1)) % MOD1;
        hash2[i] = (hash2[i - 1] * BASE2 + (s[i - 1] - 'a' + 1)) % MOD2;
    }
}

pair<ll, ll> get_hash(int l, int r) {
    ll h1 = (hash1[r] - hash1[l - 1] * power1[r - l + 1] % MOD1 + MOD1) % MOD1;
    ll h2 = (hash2[r] - hash2[l - 1] * power2[r - l + 1] % MOD2 + MOD2) % MOD2;
    return {h1, h2};
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    int n, m;
    cin >> n >> m;
    string s;
    cin >> s;
    precompute_powers(n);
    compute_hashes(s);
    map<pair<int, int>, vector<int>> hashtoind;
    for(int i = 1; i <= m; i++) {
        string x;
        cin >> x;
        debug(x);
        pair<int, int> hash1 = hashstring(x);
        debug(hash1);
        hashtoind[hash1].push_back(i);
    }
    for(int i = 1; i + n / m - 1 <= n; i += n / m) {
        cout << hashtoind[get_hash(i, i + n / m - 1)].back() << ' ';
        hashtoind[get_hash(i, i + n / m - 1)].pop_back();
        debug(s.substr(i - 1, n / m));
    }
    return 0;
}