#pragma GCC optimize("trapv")
#define int long long

const int mod1 = 1e9 + 7;
const int mod2 = 1e9 + 9;
const int base1 = 31;
const int base2 = 37;
const int maxn = 4e6 + 5;

int powmod1[maxn], powmod2[maxn];
int hashs1[maxn], hashs2[maxn];
int tmp[maxn];

void precompute_powers() {
    powmod1[0] = powmod2[0] = 1;
    for (int i = 1; i < maxn; i++) {
        powmod1[i] = (powmod1[i - 1] * base1) % mod1;
        powmod2[i] = (powmod2[i - 1] * base2) % mod2;
    }
}

void compute_hash(const string &s, int n) {
    for (int i = 1; i <= n; i++) {
        assert(i < s.size());
        hashs1[i] = (hashs1[i - 1] * base1 + s[i]) % mod1;
        hashs2[i] = (hashs2[i - 1] * base2 + s[i]) % mod2;
    }
}

pair<int, int> get_hash(int l, int r) {
    int hash1 = (hashs1[r] - hashs1[l - 1] * powmod1[r - l + 1] % mod1 + mod1) % mod1;
    int hash2 = (hashs2[r] - hashs2[l - 1] * powmod2[r - l + 1] % mod2 + mod2) % mod2;
    return {hash1, hash2};
}

signed main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);

    string s, t;
    getline(cin, s);
    cin >> t;
    int k;
    cin >> k;
    
    int n = s.size();
    int m = t.size();
    assert(n < maxn and m < maxn);
    s = " " + s; 
    t = " " + t; 

    precompute_powers();
    compute_hash(s, n);

    pair<int, int> hash_t = {0, 0};
    for (int i = 1; i <= m; i++) {
        assert(i < t.size());
        hash_t.first = (hash_t.first * base1 + t[i]) % mod1;
        hash_t.second = (hash_t.second * base2 + t[i]) % mod2;
    }

    for (int i = 1; i + m - 1 <= n; i++) {
        tmp[i] = (get_hash(i, i + m - 1) == hash_t);
    }
    for(int i = 1; i <= n; i++) {
        tmp[i] += tmp[i - 1];
    }
    map<int, int> stor;
    long long ans = 0;

    for (int i = max(k, m); i <= n; i++) {
        ans += tmp[i - m + 1] - tmp[i - k] > 0;
    }

    cout << ans << '\n';
    return 0;
}
