long long calc(int n) {
    if(n == 1) {
        return 1;
    }
    return n * n * n + calc(n - 1);
}
int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    int n;
    cin >> n;
    cout << calc(n);
}
