#define int long long
int ans = 0;
void generate(int current, int open, int close, int n) {
    if ((int) current == n) {
        ans++;
        return;
    }

    if (open < n /2 ) {
        generate(current + 1, open + 1, close, n);
    }

    if (close < open) {
        generate(current + 1, open, close + 1, n);
    }
}

signed main() {
    int N;
    cin >> N;
    generate(0, 0, 0, N);

    cout << ans;

    return 0;
}