int min_unassigned_cows = INT_MAX;

void bacctracc(const vector<int> &a, int index, int barn1_sum, int barn2_sum, int unassigned_count) {
    if (index == a.size()) {
        if (barn1_sum == barn2_sum) {
            min_unassigned_cows = min(min_unassigned_cows, unassigned_count);
        }
        return;
    }

    bacctracc(a, index + 1, barn1_sum + a[index], barn2_sum, unassigned_count);

    bacctracc(a, index + 1, barn1_sum, barn2_sum + a[index], unassigned_count);

    bacctracc(a, index + 1, barn1_sum, barn2_sum, unassigned_count + 1);
}

int main() {
    int n;
    cin >> n;
    vector<int> a(n);
    for (int i = 0; i < n; ++i) {
        cin >> a[i];
    }

    bacctracc(a, 0, 0, 0, 0);
    cout << min_unassigned_cows << endl;

    return 0;
}