#pragma GCC optimize("Ofast")
#pragma GCC target("arch=skylake")
const int MAX_SQRT = 31622;
double maxtime = 0;
class Timer { private: chrono::time_point <chrono::steady_clock> Begin, End; public: Timer () : Begin(), End ()
{ Begin = chrono::steady_clock::now(); } ~Timer () { End = chrono::steady_clock::now();
/*cerr << "Duration: " << ((chrono::duration <double>)(End - Begin)).count() << "s\n";*/ maxtime += ((chrono::duration <double>)(End - Begin)).count();} };

bool isprime[MAX_SQRT + 1];

int nearestmul(int i, int n) {
    return (i + n - 1) / n * n;
}
long long a, b;

vector<int> sieve() {
    memset(isprime, 1, sizeof isprime);
    for(int i = 2; i * i <= MAX_SQRT; i++) {
        if(isprime[i]) {
            for(int j = i * i; j <= MAX_SQRT; j += i) {
                isprime[j] = false;
            }
        }
    }
    vector<int> primes;
    for(int i = 2; i <= MAX_SQRT; i++) {
        if(isprime[i]) {
            primes.push_back(i);
        }
    }
    return primes;
}
bool isPrime[(int) 1e6 + 5];
void segmentedSieve(long long L, long long R, vector<int> &primes) {
    // generate all primes up to sqrt(R)
    long long lim = sqrt(R);
    memset(isPrime, 1, sizeof isPrime);
    for (long long i : primes)
        for (long long j = max(i * i, (L + i - 1) / i * i); j <= R; j += i)
            isPrime[j - L] = false;
    if (L == 1)
        isPrime[0] = false;
}
vector<int> primes1;
int countDivisors(long long x, const vector<int>& primes) {
    int count = 1;
    if(isPrime[x - a]) return 2;
    for(int i:primes1) {
        if(i * i > x or x - a <= 0 or x <= i) {
            break;
        }
        int cnt = 0;
        while(x % i == 0) {
            cnt++;
            x /= i;
        }
        count *= cnt + 1;
    }
    if(x == 1) return count;
    if(x <= MAX_SQRT and isprime[x]) {
        return count * 2;
    }
    
    for(int i:primes) {
        if(i * i > x) {
            break;
        }
        int cnt = 0;
        while(x % i == 0) {
            cnt++;
            x /= i;
        }
        count *= cnt + 1;
    }
    if(x > 1) {
        count *= 2;
    }
    return count;
}

void solve() {
    cin >> a >> b;
    
    vector<int> primes = sieve();
    segmentedSieve(a, b, primes);
    for(int i = 1; i < b - a; i++) {
        if(isPrime[i]) primes1.push_back(i + a);
    }
    int q = 0, t = 0;
    long long k = numeric_limits<long long>::max();

    for (long long x = a; x <= b; ++x) {
        Timer a;
        
        int divisors = countDivisors(x, primes);
        if (divisors > q) {
            q = divisors;
            k = x;
            t = 1;
        } else if (divisors == q) {
            t++;
        }
    }
    debug(maxtime / (b - a +1));
    cout << q << " " << k << " " << t << endl;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    solve();
    return 0;
}
