int findMinInsertionsDP(string str, int n) {
  int dp[n][n], l, h, gap;
  memset(dp, 0, sizeof(dp));
  for (gap = 1; gap < n; ++gap)
    for (l = 0, h = gap; h < n; ++l, ++h) dp[l][h] = (str[l] == str[h]) ? dp[l + 1][h - 1] : (min(dp[l][h - 1], dp[l + 1][h]) + 1);
  return dp[0][n - 1];
}
int main() {
  int n;
  cin >> n;
  string str;
  cin >> str;
  cout << findMinInsertionsDP(str, n);
  return 0;
}