#include <iostream>
#include <vector>
#include <climits>
#include <algorithm>
using namespace std;

struct op {
    char t;
    long long a; // Changed to long long
};
long long cursor; // Changed to long long

int main() {
    long long n, q; // Changed to long long
    cin >> n >> q;
    vector<long long> a(n); // Changed to vector<long long>
    for (long long &i : a) cin >> i;
    a.insert(a.begin(), 0);
    cursor = n;
    vector<op> queries; // Changed to vector of op with long long

    for (long long i = 1; i <= q; i++) { // Changed loop variable to long long
        char c;
        long long x = 0; // Initialize as long long
        cin >> c;
        if (c == 'A') cin >> x;
        queries.push_back({c, x});
    }

    long long n1, n2; // Changed to long long
    vector<long long> l(n + 1), r(1); // Changed to vector<long long>
    vector<long long> lm(n + 1, LLONG_MIN), rm(1, LLONG_MAX); // Changed to vector<long long>
    vector<long long> l_(n + 1), r_(1); // Changed to vector<long long>

    for (long long i = 1; i <= n; i++) { // Changed loop variable to long long
        l_[i] = a[i];
        l[i] = l[i - 1] + a[i];
        lm[i] = max(lm[i - 1], l[i]);
    }

    n1 = n;
    n2 = 0;

    for (long long i = 1; i <= q; i++) { // Changed loop variable to long long
        op cur = queries[i - 1];
        if (cur.t == 'A') {
            l_.push_back(cur.a);
            l.push_back(cur.a);
            lm.push_back(LLONG_MIN);
            l[n1 + 1] = l[n1] + l_[n1 + 1];
            lm[n1 + 1] = max(lm[n1], l[n1 + 1]);
            n1++;
        }

        if (cur.t == 'Q') {
            long long ans = LLONG_MIN; // Changed to long long
            if (n1 > 0)
                ans = max(ans, lm[n1]);
            ans = max(ans, l[n1] + (n2 > 0 ? r[n2] - rm[n2] : 0));
            cout << ans << '\n';
        }

        if (cur.t == 'L') {
            if (n1 > 0) {
                r_.push_back(l_[n1]);
                r.push_back(l_[n1]);
                rm.push_back(LLONG_MAX);
                r[n2 + 1] += r[n2];
                rm[n2 + 1] = min(rm[n2], r[n2]);
                n2++;
                lm.pop_back();
                l.pop_back();
                l_.pop_back();
                n1--;
            }
        }

        if (cur.t == 'R') {
            if (n2 > 0) {
                l_.push_back(r_[n2]);
                l.push_back(r_[n2]);
                l[n1 + 1] += l[n1];
                lm.push_back(LLONG_MIN);
                lm[n1 + 1] = max(lm[n1], l[n1 + 1]);
                n1++;
                r.pop_back();
                rm.pop_back();
                r_.pop_back();
                n2--;
            }
        }

        if (cur.t == 'D') {
            if (n1 > 0) {
                lm.pop_back();
                l.pop_back();
                l_.pop_back();
                n1--;
            }
        }
    }
}